package abstration_demo;

public class Test_even_odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// wp to check the num even and odd
		
		for(int i=1;i<=10;i++)
		{
		
			
			System.out.println(i);
			
		}
		
		// even
		for(int i=1;i<=10;i++)
		{
		  if(i%2==0)
			
			System.out.println(i);
			
		}
		
		
		
		// odd
		for(int i=1;i<=10;i++)
		{
		  if(i%2!=0)
			
			System.out.println(i);
			
		}
 	}

}
